create table sys_roles (id bigint generated by default as identity, name varchar(60), primary key (id));
create table sys_users (id bigint not null, created_at timestamp not null, updated_at timestamp not null, email varchar(255), name varchar(255), password varchar(255), username varchar(255), primary key (id));
create table user_roles (user_id bigint not null, role_id bigint not null, primary key (user_id, role_id));